import React, { useState } from "react";
import styles from "./AddUser.module.css";
import Card from "../UI/Card";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";

export default function AddUser(props) {
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredAge, setEnteredAge] = useState("");
  const addUserHandler = (event) => {
    event.preventDefault();
    if (enteredUsername.trim().length === 0 || enteredAge.trim().length === 0) {
      return;
    }

      if (enteredUsername.trim().length > 1 && +enteredAge >= 1) {
        props.setUsersList((prevUsersList) => {
          return [...prevUsersList, { name: enteredUsername, age: enteredAge }];
        });
        setEnteredUsername("");
        setEnteredAge("");
      } else {
        alert("Enter data");
      }
  };

  const usernameChangeHandler = (event) => {
    setEnteredUsername(event.target.value);
  };
  const ageChangeHandler = (event) => {
    setEnteredAge(event.target.value);
  };

  return (
    <div>
      <ErrorModal title="Error" message="Something went wrong!" />
      <Card className={styles.inputForm}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">Username</label>
          <input
            id="username"
            type="text"
            value={enteredUsername}
            onChange={usernameChangeHandler}
            autoComplete="off"
          />
          <label htmlFor="age">Age (Years)</label>
          <input
            id="age"
            type="number"
            min="1"
            onChange={ageChangeHandler}
            value={enteredAge}
          />
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </div>
  );
}
